INSERT INTO `app_hrzg_widget_template` (`id`, `name`, `php_class`, `json_schema`, `twig_template`)
VALUES
	(2, 'Slider', 'hrzg\\widget\\widgets\\TwigTemplate', '{\r\n  \"title\": \"Slider Widget\",\r\n  \"type\": \"object\",\r\n  \"required\": [\"skin\"],\r\n  \"properties\": {\r\n    \"skin\": {\r\n      \"type\": \"string\",\r\n       \"enum\": [\"default\"]\r\n    },  \r\n    \"slides\": {\r\n      \"type\": \"array\",\r\n      \"format\": \"tabs\",\r\n      \"title\": \"Slides\",\r\n      \"items\": {\r\n        \"type\": \"object\",\r\n        \"title\": \"Slide\",\r\n        \"properties\": {\r\n          \"content\": {\r\n            \"type\": \"string\",\r\n            \"format\": \"string\",\r\n            \"default\": \"<img src=\'https://unsplash.it/1600/600/?random\'/>\"\r\n          },\r\n          \"caption\": {\r\n            \"type\": \"string\",\r\n            \"default\": \"Quisque a lectus.\"\r\n          }\r\n        }\r\n      }\r\n    }\r\n  }\r\n}', '{{ use (\'yii/bootstrap\') }}\r\n\r\n<div class=\"{{ skin }}\">\r\n{{ carousel_widget(\r\n    {\r\n        id: \'header\',\r\n        items: slides\r\n    }\r\n) }}\r\n</div>'),
	(3, 'Jumbotron with background image', 'hrzg\\widget\\widgets\\TwigTemplate', '{\r\n  \"title\": \"Jumbotron\",\r\n  \"type\": \"object\",\r\n  \"id\": \"jumbotron\",\r\n  \"required\": [\"skin\"],\r\n  \"properties\": {\r\n    \"skin\": {\r\n          \"type\": \"string\",\r\n           \"enum\": [\"default\"]\r\n    },      \r\n    \"title\": {\r\n      \"type\": \"string\",\r\n      \"default\": \"Aliquam tincidunt mauris eu risus.\",\r\n      \"minLength\": 4\r\n    },\r\n    \"image\": {\r\n      \"type\": \"string\",\r\n      \"default\": \"https://unsplash.it/1600/1200/?random\"\r\n    }\r\n  }\r\n}', '<div class=\"widget-content jumbotron text-center {{ skin }}\" style=\"background-image: url({{ image }});\">\r\n    <div class=\"container\">\r\n        <h1>\r\n            {{ title }}\r\n        </h1>\r\n    </div>\r\n</div>'),
	(4, 'Content (call to action)', 'hrzg\\widget\\widgets\\TwigTemplate', '{\n    \"title\": \"Jumbotron\",\n    \"type\": \"object\",\n    \"id\": \"jumbotron\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"skin\": {\n            \"type\": \"string\",\n            \"enum\": [\n                \"default\",\n                \"inverse\"\n            ]\n        },\n        \"title\": {\n            \"type\": \"string\",\n            \"default\": \"Senectus et netus et malesuada fames ac turpis\"\n        },\n        \"subline\": {\n            \"type\": \"string\",\n            \"default\": \"Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante.\"\n        },\n        \"blocks\": {\n            \"type\": \"array\",\n            \"format\": \"default\",\n            \"title\": \"Block\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Block\",\n                \"properties\": {\n                    \"text_html\": {\n                        \"type\": \"string\",\n                        \"default\": \"<p>Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante. Donec eu libero sit amet quam egestas semper. Aenean ultricies mi vitae est. Mauris placerat eleifend leo.<\\/p>\",\n                        \"format\": \"html\",\n                        \"options\": {\n                            \"wysiwyg\": true\n                        }\n                    }\n                }\n            }\n        },\n        \"button_text\": {\n            \"type\": \"string\",\n            \"default\": \"Aenean\"\n        },\n        \"button_url\": {\n            \"type\": \"string\",\n            \"default\": \"#\"\n        }\n    }\n}', '<div class=\"widget-content call-to-action text-center bg-{{ skin }}\">\r\n    <div class=\"container\">\r\n        <h2>\r\n            {{ title }}\r\n            <br/>\r\n            <small>{{ subline }}</small><br/>\r\n        </h2>\r\n        {% for block in  blocks %}\r\n            {{ block.text_html|raw }}\r\n        {% endfor %}\r\n        {% if (button_text) %}\r\n        <a href=\"{{ button_url }}\" class=\"btn btn-primary\">{{ button_text }}</a>\r\n        {% endif %}\r\n    </div>\r\n</div>\r\n'),
	(5, 'Content', 'hrzg\\widget\\widgets\\TwigTemplate', '{\r\n  \"title\": \"Jumbotron\",\r\n  \"type\": \"object\",\r\n  \"id\": \"jumbotron\",\r\n  \"required\": [\"skin\"],\r\n  \"properties\": {\r\n    \"skin\": {\r\n      \"type\": \"string\",\r\n       \"enum\": [\"default\",\"inverse\"]\r\n    },      \r\n    \"title\": {\r\n      \"type\": \"string\",\r\n      \"default\": \"Malesuada fames ac turpis\"\r\n    },\r\n    \"subline\": {\r\n      \"type\": \"string\",\r\n      \"default\": \"Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante.\"\r\n    },\r\n    \"blocks\": {\r\n      \"type\": \"array\",\r\n      \"format\": \"default\",\r\n      \"title\": \"Block\",\r\n      \"items\": {\r\n        \"type\": \"object\",\r\n        \"title\": \"Block\",\r\n        \"properties\": {\r\n          \"text_html\": {\r\n            \"type\": \"string\",\r\n            \"default\": \"<p>Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante. Donec eu libero sit amet quam egestas semper. Aenean ultricies mi vitae est. Mauris placerat eleifend leo.</p>\",\r\n            \"format\": \"html\",\r\n            \"options\": {\r\n              \"wysiwyg\": true\r\n            }\r\n          }\r\n        }\r\n      }\r\n    }\r\n  }\r\n}', '<div class=\"widget-content content {{ skin }}\">\r\n    <div class=\"container\">\r\n    <h2>\r\n        <small>{{subline}}</small>\r\n        {{ title }}\r\n    </h2>\r\n    \r\n    {% for block in  blocks %}\r\n        {{ block.text_html|raw }}\r\n    {% endfor %}\r\n    \r\n    </div>\r\n</div>'),
	(9, 'Content (1-4 blocks)', 'hrzg\\widget\\widgets\\TwigTemplate', '{\n    \"title\": \"Properties\",\n    \"type\": \"object\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"skin\": {\n            \"type\": \"string\",\n            \"enum\": [\n                \"default\",\n                \"inverse\"\n            ]\n        },\n        \"blocks\": {\n            \"type\": \"array\",\n            \"format\": \"default\",\n            \"title\": \"Block\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Block\",\n                \"properties\": {\n                    \"icon\": {\n                        \"type\": \"string\",\n                        \"enum\": [\n                            \"cog\",\n                            \"user\",\n                            \"500px\",\n                            \"android\",\n                            \"apple\",\n                            \"plus\",\n                            \"check\"\n                        ]\n                    },\n                    \"title\": {\n                        \"type\": \"string\",\n                        \"default\": \"Senectus et netus et malesuada fames ac turpis egestas.\"\n                    },\n                    \"text_html\": {\n                        \"type\": \"string\",\n                        \"default\": \"<p>Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante. Donec eu libero sit amet quam egestas semper. Aenean ultricies mi vitae est. Mauris placerat eleifend leo.<\\/p>\",\n                        \"format\": \"html\",\n                        \"options\": {\n                            \"wysiwyg\": true\n                        }\n                    },\n                    \"button_text\": {\n                        \"type\": \"string\",\n                        \"default\": \"Aenean\"\n                    },\n                    \"button_url\": {\n                        \"type\": \"string\",\n                        \"default\": \"#\"\n                    }\n                }\n            }\n        }\n    }\n}', '<div class=\"widget-content blocks-3 bg-{{ skin }}\">\r\n    <div class=\"container\">\r\n        <div class=\"row\">\r\n        {% for block in  blocks %}\r\n        <div class=\"col-sm-{{ 12 / blocks|length }}  text-center\">\r\n            <div class=\"icon\">\r\n                <i class=\"fa fa-{{ block.icon }}\"></i>\r\n            </div>\r\n            <h3>{{ block.title }}</h3>\r\n            <hr/>\r\n            {{ block.text_html|raw }}\r\n            <p>\r\n                <a href=\"{{block.button_url}}\" class=\"btn btn-default\">{{block.button_text}}</a>\r\n            </p>\r\n        </div>\r\n        {% endfor %}\r\n        </div>\r\n    </div>\r\n</div>\r\n'),
	(13, 'Image', 'hrzg\\widget\\widgets\\TwigTemplate', '{\r\n    \"title\": \"Basic\",\r\n    \"type\": \"object\",\r\n    \"required\": [\"skin\"],    \r\n    \"properties\": {\r\n        \"skin\": {\r\n          \"type\": \"string\",\r\n           \"enum\": [\"default\"]\r\n        },\r\n        \"image\": {\r\n            \"type\": \"string\",\r\n            \"default\": \"https://unsplash.it/1600/600/?random\"\r\n        }\r\n    }\r\n}', '<div class=\"jumbotron text-center {{ skin }}\" style=\"background-image: url({{ image }}); padding: 100px 0\">\r\n    &nbsp;\r\n</div>'),
	(17, 'Header with background video', 'hrzg\\widget\\widgets\\TwigTemplate', '{\n    \"title\": \"Video Widget\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"headline\": {\n            \"type\": \"string\",\n            \"default\": \"Herzlich Willkommen\"\n        },\n        \"youtube_id\": {\n            \"type\": \"string\",\n            \"default\": \"Rk6_hdRtJOE\"\n        }\n    }\n}', '<div style=\"position: relative\">\r\n    <div class=\"embed-responsive embed-responsive-16by9\">\r\n        <div id=\"background\">\r\n           <iframe id=\'player\' width=\"100%\" height=\"100%\" src=\"https://www.youtube.com/embed/{{ youtube_id }}?rel=0&amp;controls=0&amp;showinfo=0&amp;autoplay=1&amp;loop=1&amp;playlist={{ youtube_id }}&amp;enablejsapi=1&version=3\" frameborder=\"0\"></iframe>\r\n        </div>\r\n    </div>\r\n         <div class=\"video-front\">\r\n        <h1>{{ headline }}</h1>\r\n        </div>\r\n</div>'),
	(19, 'Footer', 'hrzg\\widget\\widgets\\TwigTemplate', '{\n    \"title\": \"Jumbotron\",\n    \"type\": \"object\",\n    \"id\": \"jumbotron\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"skin\": {\n            \"type\": \"string\",\n            \"enum\": [\n                \"default\",\n                \"inverse\"\n            ]\n        },\n        \"blocks\": {\n            \"type\": \"array\",\n            \"format\": \"default\",\n            \"title\": \"Block\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Block\",\n                \"properties\": {\n                    \"text_html\": {\n                        \"type\": \"string\",\n                        \"default\": \"<p>\\u00a9 2016 dmstr | Imprint | Legal<\\/p>\",\n                        \"format\": \"html\",\n                        \"options\": {\n                            \"wysiwyg\": true\n                        }\n                    }\n                }\n            }\n        }\n    }\n}', '<div class=\"widget-content content {{ skin }}\">\r\n    <div class=\"container\">\r\n        <div class=\"pull-left\">\r\n            {% for block in  blocks %}\r\n            {{ block.text_html|raw }}\r\n            {% endfor %}\r\n        </div>\r\n        \r\n    \r\n    </div>\r\n</div>\r\n\r\n'),
	(20, 'Content (3 blocks, multiline)', 'hrzg\\widget\\widgets\\TwigTemplate', '{\n    \"title\": \"ContentModal\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"blocks\": {\n            \"type\": \"array\",\n            \"format\": \"tabs\",\n            \"title\": \"Content\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Modal\",\n                \"properties\": {\n                    \"image_url\": {\n                        \"type\": \"string\"\n                    },\n                    \"brandname\": {\n                        \"type\": \"string\"\n                    },\n                    \"subline\": {\n                        \"type\": \"string\"\n                    },\n                    \"text_html\": {\n                        \"type\": \"string\",\n                        \"format\": \"html\",\n                        \"options\": {\n                            \"wysiwyg\": true\n                        }\n                    },\n                    \"link_url\": {\n                        \"type\": \"string\"\n                    },\n                    \"link_text\": {\n                        \"type\": \"string\"\n                    },\n                    \"link_target\": {\n                        \"type\": \"boolean\",\n                        \"format\": \"checkbox\",\n                        \"description\": \"Link in einem neuen Tab \\u00f6ffnen\"\n                    }\n                }\n            }\n        }\n    }\n}', '<div class=\"row\">\r\n    \r\n    {% for block in blocks %}\r\n    <div class=\"col-md-4 col-sm-6\">\r\n        <img src=\"{{ block.image_url }}\" class=\"img-responsive\"/>\r\n        <h4>{{ block.brandname }}</h4>\r\n        <h3>{{ block.subline}}</h3>\r\n        {{ block.text_html |raw }}\r\n        {% if block.link_url != \"\" %}\r\n        <a href=\"{{ block.link_url }}\" {% if block.link_target == \"false\" %}target=\"_blank\"{% endif %}>{{ block.link_text }}</a> \r\n            {% endif %}\r\n    </div>\r\n    {% if (loop.index % 3 ) == 0 %}\r\n        <div class=\"clearfix\"></div>\r\n    {% endif %}        \r\n    {% endfor %}\r\n</div>\r\n');
